import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ReactMarkdown from "react-markdown";
import MainLayout from "../Layouts/MainLayout/MainLayout";
import { Client } from "../../prismic-config";
const code =
  '```x = "global"\n\ndef foo():\n    print("x inside:", x)\n\n\nfoo()\nprint("x outside:", x)```';
export default function PostContainer({ match }) {
  const [ArticleData, setArticleData] = useState(null);
  const id = match.params.id;
  useEffect(() => {
    const fetchPrismicData = async () => {
      const article = await Client.getByUID("sortcoder_blog", id);
      setArticleData(article);
      console.log(article);
    };
    fetchPrismicData();
  }, []);
  if (ArticleData == null) {
    return (
      <>
        <div>Loading...</div>
      </>
    );
  } else {
    return (
      <>
        <MainLayout>
          <PostWrapper>
            <Container>
              <PostHeader>
                <h1>{ArticleData.data.title[0].text}</h1>
                <p>{ArticleData.data.subtext[0].text}</p>
                <img src={ArticleData.data.image.url} />
              </PostHeader>
              <ContentWrapper>
                <h2>#1 Write great subject lines</h2>
                <p>
                  It’s not uncommon for writers to spend hours, days, and even
                  weeks creating an effective email. Whether it’s the latest
                  edition of a newsletter or a surprise announcement for a new
                  product, crafting a great message takes research, planning,
                  and editing.{" "}
                </p>
                <ContentImg>
                  <img
                    src="https://ghost.org/blog/content/images/2021/07/email-subject-line-branding-example.png"
                    alt="img"
                    loading="lazy"
                    width="100%"
                  />
                  <figcaption>
                    crafting a great message takes research, planning, and
                    editing.crafting a great message takes research, planning,
                    and editing.
                  </figcaption>
                </ContentImg>
                <p>
                  It’s not uncommon for writers to spend hours, days, and even
                  weeks creating an effective email. Whether it’s the latest
                  edition of a newsletter or a surprise announcement for a new
                  product, crafting a great message takes research, planning,
                  and editing.
                </p>
                <ul>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                </ul>

                <div>
                  <ReactMarkdown children={code} />
                </div>
              </ContentWrapper>
            </Container>
          </PostWrapper>
        </MainLayout>
      </>
    );
  }
}
const PostWrapper = styled.div`
  /* background-color: navajowhite; */
`;
const Container = styled.div`
  background-color: #fff;
  width: 100%;
  max-width: 1172px;
  margin: 0 auto;
`;
const PostHeader = styled.div`
  text-align: left;
  padding: 70px 170px 30px;
  h1 {
    font-weight: 600;
    font-size: 48px;
    margin-bottom: 11px;
  }
  p {
    font-size: 22px;
    margin-top: 20px;
    line-height: 1.5em;
    color: #808286;
  }
`;
const ContentWrapper = styled.div`
  text-align: left;
  padding: 30px 170px 50px;
  display: flex;
  flex-direction: column;
  line-height: 1.8;
  font-size: 18px;
  p {
    margin-bottom: 30px;
        font-size: 21px;
}
  }

  h2 {
    font-size: 28px;
    margin-top: 25px;
    margin-bottom: 12px;
  }
  ul {
    padding: 0 30px;
    margin-left: 30px;
    margin-bottom: 30px;
    li {
      margin: 10px 0;
    }

`;
const ContentImg = styled.div`
  margin: 16px 0 40px;
  flex-direction: column;
  figcaption {
    line-height: 1.5em;
    text-align: center;
    font-size: 14px;
    margin-top: 15px;
    margin-left: 16px;
    margin-right: 16px;
  }
`;
