import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
export default function BlogCard(params) {
  return (
    <>
      <BlogCardContainer>
        <NavLink>
          <img
            src="https://ghost.org/blog/content/images/size/w1000/2021/07/publishing-metrics--1-.png"
            alt="img"
            width="100%"
            height="100%"
          />
        </NavLink>
        <BlogContent>
          <h2>How to increase the open rates of your email newsletter</h2>
          <p>
            Writing a great email takes time, creativity, and practice. Getting
            readers to open those emails is no different. Apply these tips to
            ensure your hard work gets read.
          </p>
          <AuthorWrapper>
            <div>
              <img
                src="https://ghost.org/blog/content/images/size/w1000/2021/07/publishing-metrics--1-.png"
                alt="img"
                width="34px"
                height="34px"
              />
            </div>
            <AuthorDetails>
              <span>By: Ritik</span>
              <span>19 Jul 2021</span>
            </AuthorDetails>
          </AuthorWrapper>
        </BlogContent>
      </BlogCardContainer>
    </>
  );
}
const BlogCardContainer = styled.div`
  padding: 0 20px;
  display: flex;
  flex-direction: column;
  margin-bottom: 30px;
  flex: 1 1 301px;
`;
const BlogContent = styled.div`
  width: 100%;
  max-width: 896px;
  text-align: left;
  display: flex;
  flex-direction: column;
  h2 {
    margin-top: 15px;
    font-size: 22px;
    line-height: 32px;
    margin-bottom: 8px;
  }
  p {
    font-size: 16px;
    line-height: 20px;
    color: #6d6d6d;
    margin-bottom: 16px;
  }
`;
const NavLink = styled(Link)`
  height: 200px;
  width: 100%;
  display: block;
  img {
    border-radius: 5px;
    object-fit: cover;
    width: 100%;
  }
`;
const AuthorWrapper = styled.div`
  img {
    border-radius: 100%;
  }
`;
