import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ReactMarkdown from "react-markdown";
import MainLayout from "../Layouts/MainLayout/MainLayout";
import { Client } from "../../prismic-config";
import SyntaxHighlighter from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";
export default function PostContainer({ match }) {
  const [ArticleData, setArticleData] = useState(null);
  const id = match.params.id;
  useEffect(() => {
    const fetchPrismicData = async () => {
      const article = await Client.getByUID("sortcoder_blog", id);
      setArticleData(article);
    };
    console.log("j");
    fetchPrismicData();
  }, [id]);
  if (ArticleData == null) {
    return (
      <>
        <div>Loading...</div>
      </>
    );
  } else {
    return (
      <>
        <MainLayout>
          <PostWrapper>
            <Container>
              <PostHeader>
                <h1>{ArticleData.data.title[0].text}</h1>
                <p>{ArticleData.data.subtext[0].text}</p>
                <div>
                  <img
                    src={ArticleData.data.image.url}
                    alt="img"
                    width="100%"
                  ></img>
                </div>
              </PostHeader>
              <ContentWrapper>
                {ArticleData.data.body[0].primary.text.map((text) => {
                  switch (text.type) {
                    case "heading2":
                      return <h2>{text.text}</h2>;
                    case "paragraph":
                      return <p>{text.text}</p>;
                    case "preformatted":
                      return (
                        <div>
                          <SyntaxHighlighter language="python" style={docco}>
                            <ReactMarkdown
                              children={text.text}
                              className="language-python"
                            />
                          </SyntaxHighlighter>
                        </div>
                      );
                    case "image":
                      return (
                        <ContentImg>
                          <img
                            src={text.url}
                            alt="img"
                            loading="lazy"
                            width="100%"
                          />
                          <figcaption>{text.alt}</figcaption>
                        </ContentImg>
                      );
                    default:
                      return null;
                  }
                })}
                <ul>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                </ul>
              </ContentWrapper>
            </Container>
          </PostWrapper>
        </MainLayout>
      </>
    );
  }
}
const PostWrapper = styled.div`
  /* background-color: navajowhite; */
`;
const Container = styled.div`
  background-color: #fff;
  width: 100%;
  max-width: 1172px;
  margin: 0 auto;
`;
const PostHeader = styled.div`
  text-align: left;
  padding: 70px 170px 30px;
  h1 {
    font-weight: 600;
    font-size: 48px;
    margin-bottom: 11px;
  }
  p {
    font-size: 22px;
    margin-top: 20px;
    line-height: 1.5em;
    color: #808286;
  }
`;
const ContentWrapper = styled.div`
  text-align: left;
  padding: 30px 170px 50px;
  display: flex;
  flex-direction: column;
  line-height: 1.8;
  font-size: 18px;
  p {
    margin-bottom: 30px;
        font-size: 21px;
}
  }

  h2 {
    font-size: 28px;
    margin-top: 25px;
    margin-bottom: 12px;
  }
  ul {
    padding: 0 30px;
    margin-left: 30px;
    margin-bottom: 30px;
    li {
      margin: 10px 0;
    }

`;
const ContentImg = styled.div`
  margin: 16px 0 40px;
  flex-direction: column;
  figcaption {
    line-height: 1.5em;
    text-align: center;
    font-size: 14px;
    margin-top: 15px;
    margin-left: 16px;
    margin-right: 16px;
  }
`;
