import React from "react";
import styled from "styled-components";
import MainLayout from "../Layouts/MainLayout/MainLayout";
export default function PostContainer(params) {
  return (
    <>
      <MainLayout>
        <PostWrapper>
          <Container>
            <PostHeader>
              <h1>How to increase the open rates of your email newsletter</h1>
              <p>
                Writing a great email takes time, creativity, and practice.
                Getting readers to open those emails is no different. Apply
                these tips to ensure your hard work gets read.
              </p>
            </PostHeader>
            <ContentWrapper>
              <h2>#1 Write great subject lines</h2>
              <p>
                It’s not uncommon for writers to spend hours, days, and even
                weeks creating an effective email. Whether it’s the latest
                edition of a newsletter or a surprise announcement for a new
                product, crafting a great message takes research, planning, and
                editing.{" "}
              </p>
              <ContentImg>
                <img
                  src="https://ghost.org/blog/content/images/2021/07/email-subject-line-branding-example.png"
                  alt="img"
                  loading="lazy"
                  width="100%"
                />
                <figcaption></figcaption>
              </ContentImg>
            </ContentWrapper>
          </Container>
        </PostWrapper>
      </MainLayout>
    </>
  );
}
const PostWrapper = styled.div`
  background-color: navajowhite;
`;
const Container = styled.div`
  background-color: #fff;
  width: 100%;
  max-width: 1172px;
  margin: 0 auto;
`;
const PostHeader = styled.div`
  text-align: left;
  padding: 70px 170px 50px;
  h1 {
    font-weight: 600;
    font-size: 48px;
    margin-bottom: 11px;
  }
  p {
    font-size: 22px;
    margin-top: 20px;
    line-height: 1.5em;
  }
`;
const ContentWrapper = styled.div`
  text-align: left;
  padding: 70px 170px 50px;
  display: flex;
  flex-direction: column;
  line-height: 1.8;
  p {
  }
  img {
  }
  h2 {
    font-size: 28px;
    margin-top: 25px;
    margin-bottom: 12px;
  }
`;
const ContentImg = styled.div`
  margin: 16px 0 40px;
  flex-direction: column;
`;
