import React, { useEffect, useState } from "react";
import styled from "styled-components";
// import ReactMarkdown from "react-markdown";
import MainLayout from "../Layouts/MainLayout/MainLayout";
import { Client } from "../../prismic-config";
export default function PostContainer({ match }) {
  const [ArticleData, setArticleData] = useState(null);
  const id = match.params.id;
  let li_count = -1;
  useEffect(() => {
    const fetchPrismicData = async () => {
      const article = await Client.getByUID("sortcoder_blog", id);
      setArticleData(article);
    };
    // console.log(ArticleData);
    fetchPrismicData();
  }, [id]);
  if (ArticleData == null) {
    return (
      <>
        <div>Loading...</div>
      </>
    );
  } else {
    return (
      <>
        <MainLayout>
          <PostWrapper>
            <Container>
              <PostHeader>
                <h1>{ArticleData.data.title[0].text}</h1>
                <p>{ArticleData.data.subtext[0].text}</p>
                <PostHeaderImg>
                  <img
                    src={ArticleData.data.image.url}
                    alt="img"
                    width="100%"
                  ></img>
                </PostHeaderImg>
              </PostHeader>
              <ContentWrapper>
                {ArticleData.data.body[0].primary.text.map((text, key) => {
                  switch (text.type) {
                    case "heading2":
                      return <h2 key={key}>{text.text}</h2>;
                    case "paragraph":
                      return <p key={key}>{text.text}</p>;
                    case "o-list-item":
                      if (!ol_list) {
                        ol_list = true;
                        li_count = li_count + 1;
                        return (
                          <ol id={"list-" + key}>
                            <li>{text.text}</li>
                          </ol>
                        );
                      } else {
                        li_count = li_count + 1;

                        return;
                      }
                    case "image":
                      return (
                        <ContentImg key={key}>
                          <img
                            src={text.url}
                            alt="img"
                            loading="lazy"
                            width="100%"
                          />
                          <figcaption>{text.alt}</figcaption>
                        </ContentImg>
                      );
                    default:
                      return null;
                  }
                })}
                <ul>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                  <li>
                    crafting a great message takes research, planning, and
                    editing
                  </li>
                </ul>
              </ContentWrapper>
            </Container>
          </PostWrapper>
        </MainLayout>
      </>
    );
  }
}
const PostWrapper = styled.div`
  /* background-color: navajowhite; */
`;
const Container = styled.div`
  background-color: #fff;
  width: 100%;
  max-width: 1172px;
  margin: 0 auto;
`;
const PostHeader = styled.div`
  text-align: left;
  padding: 70px 170px 30px;
  h1 {
    font-weight: 600;
    font-size: 48px;
    margin-bottom: 11px;
  }
  p {
    font-size: 22px;
    margin-top: 20px;
    line-height: 1.5em;
    color: #808286;
  }

  @media screen and (max-width: 992px) {
    padding: 70px 50px 25px;
  }
  @media screen and (max-width: 550px) {
    padding: 70px 30px 25px;
    h1 {
      font-size: 28px;
    }
    p {
      font-size: 16px;
    }
  }
`;
const ContentWrapper = styled.div`
  text-align: left;
  padding: 30px 170px 50px;
  display: flex;
  flex-direction: column;
  line-height: 1.8;
  font-size: 18px;

  p {
    margin-bottom: 30px;
    font-size: 21px;
  }

  h2 {
    font-size: 28px;
    margin-top: 25px;
    margin-bottom: 12px;
  }
  ul {
    padding: 0 30px;
    margin-left: 30px;
    margin-bottom: 30px;
    li {
      margin: 10px 0;
    }
  }
  ol {
    padding: 0 30px;
    margin-left: 30px;
    margin-bottom: 30px;
    li {
      margin: 10px 0;
    }
  }
  @media screen and (max-width: 992px) {
    padding: 30px 80px 50px;
  }
  @media screen and (max-width: 550px) {
    padding: 30px 40px 50px;
    p {
      font-size: 16px;
    }
    h2 {
      font-size: 21px;
    }
    ul {
      padding: 0;
    }
  }
`;

const PostHeaderImg = styled.div`
  width: 100%;
  max-width: 500px;
  margin: 0 auto;
  margin-top: 30px;
`;

const ContentImg = styled.div`
  margin: 16px 0 40px;
  flex-direction: column;
  max-width: 600px;
  margin: 0 auto;
  figcaption {
    line-height: 1.5em;
    text-align: center;
    font-size: 14px;
    margin-top: 15px;
    margin-left: 16px;
    margin-right: 16px;
  }
`;
