import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import CourseCard from "../../Components/Cards/CourseCard/CourseCard";
import Header from "../../Components/Header/Header";
export default function DataScience() {
  return (
    <>
      <Container>
        <Header />
        <HeroWrapper>
          <LeftWrapper src="/assets/left_py.svg" height="400px"></LeftWrapper>
          <ContentWrapper>
            <img src="/assets/g_sortcoder.png" alt="Logo" width="150px"></img>
            <h1>Build Data Science Skills online with us.</h1>
            <Button>Get Started</Button>
          </ContentWrapper>
          <RightWrapper src="/assets/left_py.svg" height="400px"></RightWrapper>
        </HeroWrapper>
        <SectionWrapper>
          <Section1Wrapper>
            <h1>What is DataScience?</h1>
            <p>
              Learn the data skills you need online at your own pace—from
              non-coding essentials to data science and machine learning.
            </p>
          </Section1Wrapper>
          <Section2Wrapper>
            <Section2Container>
              <Section2ContentWrapper>
                <h1>
                  Start Learning <span>for free</span>
                </h1>
                <div>
                  {data.map((d) => (
                    <CourseCard data={d} />
                  ))}
                </div>
              </Section2ContentWrapper>
            </Section2Container>
          </Section2Wrapper>
          <Section3Wrapper>
            <Section3Container>
              <Section3Left>
                <h3>
                  <span>Colab</span> Workspace
                </h3>
                <h1>Get started with your own data analysis in seconds</h1>
                <p>
                  Anyone can execute, write and share their data analysis code
                  through the browser,
                </p>
                <Button>How it works</Button>
              </Section3Left>
              <Section3Right></Section3Right>
            </Section3Container>
          </Section3Wrapper>
          <Section4Wrapper></Section4Wrapper>
        </SectionWrapper>
      </Container>
    </>
  );
}
const Container = styled.div`
  background-image: linear-gradient(rgba(0, 0, 0, 0.63), rgba(0, 0, 0, 0.623)),
    url("/assets/blur.jpg");
`;
const HeroWrapper = styled.div`
  height: 100vh;
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  justify-content: space-between;
  padding: 0 40px;
  align-items: center;
`;
const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  color: white;
  font-family: "Noto Sans TC", sans-serif;
  align-items: center;
  padding: 0 10px;
  h1 {
    color: #fff;
    margin: 0;
    font-size: 60px;
  }
  img {
    margin: 0;
    padding-bottom: 20px;
  }
`;
const LeftWrapper = styled.img`
  /* background-image: url('/assets/left_py.svg'); */
`;
const RightWrapper = styled.img``;

const Button = styled(Link)`
  padding: 12px 15px;
  background-color: #0284fe;
  border-radius: 5px;
  color: white;
  margin-top: 50px;
  cursor: pointer;
  :hover {
    box-shadow: 0 0 5px #2196f300, 0 0 10px #2196f300, 0 0 40px #2196f39c;
    transition: 0.3s ease;
  }
`;
const SectionWrapper = styled.div`
  background-color: tomato;
`;
const Section1Wrapper = styled.div`
  background-color: #7933ff;
  padding: 100px 0;
  color: #fff;
  h1 {
    font-size: 50px;
    letter-spacing: -1px;
  }
  p {
    font-size: 24px;
    max-width: 548px;
    margin: 0 auto;
    padding: 24px 0;
  }
`;
const Section2Wrapper = styled.div`
  background-color: #fffbf3;
`;
const Section2Container = styled.div`
  width: 100%;
  max-width: 1172px;
  margin: 0 auto;
  padding: 50px 0;
`;
const Section2ContentWrapper = styled.div`
  text-align: left;
  h1 {
    color: #000;
    margin-bottom: 12px;
    span {
      color: #7933ff;
    }
  }
  div {
    display: flex;
    flex-wrap: wrap;
  }
`;
const Section3Wrapper = styled.div`
  background-color: #040e18;
`;
const Section3Container = styled.div`
  max-width: 1172px;
  margin: 0 auto;
  padding: 90px 0;
  display: flex;
  justify-content: space-between;
`;
const Section3Left = styled.div`
  color: white;
  text-align: left;
  h1 {
    font-size: 40px;
    letter-spacing: -1px;
    margin-bottom: 8px;
  }
  h3 {
    font-size: 14px;
    text-transform: uppercase;
    color: #06bdfc;
    letter-spacing: 2px;
    font-weight: 700;
    margin-bottom: 12px;
    span {
      color: #e8720b;
    }
  }
  p {
    font-size: 18px;
    line-height: 1.5;
    margin-bottom: 16px;
  }
`;
const Section3Right = styled.div``;
const Section4Wrapper = styled.div``;
const data = [
  {
    title: "Learn Python",
    para: "From data manipulation to unit testing, gain the career-building skills you need to succeed as a Python programmer. No prior coding experience needed.",
    type: "Beginner",
  },
  {
    title: "Python Programming",
    para: "From data manipulation to unit testing, gain the career-building skills you need to succeed as a Python programmer. No prior coding experience needed.",
    type: "Intermediate",
  },
  {
    title: "Python Advance",
    para: "From data manipulation to unit testing, gain the career-building skills you need to succeed as a Python programmer. No prior coding experience needed.",
    type: "Advance",
  },
  {
    title: "Data Science",
    para: "From data manipulation to unit testing, gain the career-building skills you need to succeed as a Python programmer. No prior coding experience needed.",
    type: "Intermediate",
  },
];
